import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Assuming Tailwind CSS is configured or that utility classes will be globally available.
// If not, you might need a simple index.css file with basic styles, but we adhere to the single file mandate.

const App = () => {
  const [critters, setCritters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Note: We use '/api/animals' directly. The "proxy" setting in package.json
    // automatically directs this request to http://localhost:8080/api/animals
    const fetchCritters = async () => {
      try {
        const response = await axios.get('/api/animals');
        setCritters(response.data);
        setError(null);
      } catch (err) {
        console.error("Error fetching critters:", err);
        // Provide a user-friendly error message, especially if the backend isn't running
        setError("Failed to fetch data. Is the Node.js backend running on http://localhost:8080?");
      } finally {
        setLoading(false);
      }
    };

    fetchCritters();
  }, []);

  // --- Render Logic ---

  const renderContent = () => {
    if (loading) {
      return <div className="text-xl font-semibold text-indigo-400">Loading Critter Data...</div>;
    }

    if (error) {
      return (
        <div className="bg-red-100 border border-red-400 text-red-700 p-4 rounded-lg shadow-md max-w-lg mx-auto">
          <p className="font-bold">Connection Error:</p>
          <p>{error}</p>
          <p className="mt-2 text-sm italic">
            Please ensure you have run <code className="font-mono bg-red-200 px-1 rounded">node index.js</code> in your backend directory.
          </p>
        </div>
      );
    }

    if (critters.length === 0) {
      return (
        <div className="text-gray-500 italic text-lg">
          No critters found in the database. Add some via the backend!
        </div>
      );
    }

    return (
      <div className="mt-8 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 w-full max-w-4xl">
        {critters.map((critter) => (
          <div key={critter.id} className="bg-white p-6 rounded-xl shadow-lg hover:shadow-xl transition duration-300 transform hover:-translate-y-0.5 border border-indigo-100">
            <h3 className="text-2xl font-bold text-indigo-600 mb-2">{critter.name || 'Unnamed Critter'}</h3>
            <p className="text-gray-600 mb-1">
              <span className="font-medium text-gray-700">Species:</span> {critter.species || 'N/A'}
            </p>
            <p className="text-gray-600 mb-1">
              <span className="font-medium text-gray-700">Location:</span> {critter.location || 'Unknown'}
            </p>
            <p className="text-sm text-gray-400 mt-3 italic">
              Record ID: {critter.id}
            </p>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6 flex flex-col items-center">
      <header className="py-8 w-full max-w-4xl text-center">
        <h1 className="text-5xl font-extrabold text-gray-800 tracking-tight">
          CritterTrack Frontend
        </h1>
        <p className="text-lg text-gray-500 mt-2">
          Fetching data from Express Backend at <code className="font-mono bg-gray-200 px-1 rounded">http://localhost:8080</code>
        </p>
      </header>

      <main className="flex-grow p-4 w-full flex justify-center">
        {renderContent()}
      </main>
      
      <footer className="py-4 text-sm text-gray-400">
        &copy; 2024 CritterTrack Development
      </footer>
    </div>
  );
};

export default App;
